mapper.select:
select * 
	from board 
	where title like #{search}
	order by num DESC
	limit #{cri.startPage}, #{cri.perPageNum}

select * 
	from board 
	where author like #{search}
	order by num DESC
	limit #{cri.startPage}, #{cri.perPageNum}

select * 
	from board 
	where contents like #{search}
	order by num DESC
	limit #{cri.startPage}, #{cri.perPageNum}

mapper.update:
update board 
	set title = #{boardVo.title},
		contents = #{boardVo.contents}
	where num = #{boardVo.num}

mapper.select:
select count(num) 
	from board
	where title like #{search}

select count(num) 
	from board
	where author like #{search}

select count(num) 
	from board
	where contents like #{search}
